<?phpclass SyncTranslit extends CWidget{	public $from = '';	public $options;	public function init()	{		$assets = Yii::app()->getAssetManager()->publish(dirname(__FILE__) . '/assets');		Yii::app()->clientScript->registerCoreScript('jquery')				->registerScriptFile($assets . '/jquery.synctranslit.js');		$id = __CLASS__ . '#' . md5($this->from);		$options = CJavaScript::encode($this->options);		Yii::app()->getClientScript()->registerScript($id, "$('#{$this->from}').syncTranslit({$options});", CClientScript::POS_READY);	}}